@Test public void doesNotMatchMultipleIncorrectFieldsInExpectation(){
assertFalse(match(new Expectation(request().withPath("some_path").withMethod("GET").withBody("some_body").withKeepAlive(true).withSecure(true)),request().withPath("some_other_path").withMethod("POST").withBody("some_other_body").withKeepAlive(false).withSecure(false)));
assertThat(response.getBodyAsString(),is(LOG_DATE_FORMAT.format(new Date(TimeService.currentTimeMillis())) + " - request:" + NEW_LINE+ NEW_LINE+ "  {"+ NEW_LINE+ "    \"method\" : \"POST\","+ NEW_LINE+ "    \"path\" : \"some_other_path\","+ NEW_LINE+ "    \"keepAlive\" : false,"+ NEW_LINE+ "    \"secure\" : false,"+ NEW_LINE+ "    \"body\" : \"some_other_body\""+ NEW_LINE+ "  }"+ NEW_LINE+ NEW_LINE+ " didn't match expectation:"+ NEW_LINE+ NEW_LINE+ "  {"+ NEW_LINE+ "    \"id\" : \""+ UUIDService.getUUID()+ "\","+ NEW_LINE+ "    \"priority\" : 0,"+ NEW_LINE+ "    \"httpRequest\" : {"+ NEW_LINE+ "      \"method\" : \"GET\","+ NEW_LINE+ "      \"path\" : \"some_path\","+ NEW_LINE+ "      \"keepAlive\" : true,"+ NEW_LINE+ "      \"secure\" : true,"+ NEW_LINE+ "      \"body\" : \"some_body\""+ NEW_LINE+ "    },"+ NEW_LINE+ "    \"times\" : {"+ NEW_LINE+ "      \"unlimited\" : true"+ NEW_LINE+ "    },"+ NEW_LINE+ "    \"timeToLive\" : {"+ NEW_LINE+ "      \"unlimited\" : true"+ NEW_LINE+ "    }"+ NEW_LINE+ "  }"+ NEW_LINE+ NEW_LINE+ " because:"+ NEW_LINE+ NEW_LINE+ "  method didn't match: "+ NEW_LINE+ "  "+ NEW_LINE+ "    string or regex match failed expected:"+ NEW_LINE+ "  "+ NEW_LINE+ "      GET"+ NEW_LINE+ "  "+ NEW_LINE+ "     found:"+ NEW_LINE+ "  "+ NEW_LINE+ "      POST"+ NEW_LINE+ NEW_LINE+ NEW_LINE));
}