    {
        locks.add(new DummyLock());
    }

    locks.add(new ThrowingLock());

    for (int i = 0; i < 5; i++)
    {
        locks.add(new DummyLock());
    }

    new LockCollection(locks).close();

    for (DistributedLock lock : locks)
    {
