        waitForDocs(numDocs, indexer);
        logger.info("--> {} docs indexed", numDocs);

        logger.info("--> starting relocations...");
        int nodeShiftBased = numberOfReplicas; // if we have replicas shift those
        for (int i = 0; i < numberOfRelocations; i++) {
