    params = new HashMap<>();
    params.put("SomeKey", Lists.newArrayList("SomeVeryNewValue2"));
    IntegerResponse job2 = api.jobsApi().buildWithParameters(null,"QueueTestSingleParam", params);
    assertNotNull(job2);
