                  ));
  m = p.getMatcher(doc.get(CoreAnnotations.TokensAnnotation.class));
  match = m.find();
  assertTrue(match);
  assertEquals(0, m.groupCount());
  assertEquals("London in 604.", m.group());
  match = m.find();
  assertFalse(match);
}

