  assertThat(later.read(), hasItems("C", "D"));
  assertFalse(later.contains("A").read());

  // clear
  value.clear();
  assertThat(value.read(), Matchers.emptyIterable());
  assertThat(underTest.state(NAMESPACE_1, STRING_SET_ADDR), equalTo(value));
