    assertTrue(STRING_METHOD_RETURNS_STRING.isSupported(subClass2));
    assertFalse(STRING_METHOD_RETURNS_INT.isSupported(subClass2));
    assertTrue(VOID_METHOD_RETURNS_ANY.isSupported(subClass2));
